// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.9;

/// @title Math functions that do not check inputs or outputs
/// @notice Contains methods that perform common math functions but do not do any overflow or underflow checks
library UnsafeMath {
  /// @notice Returns ceil(x / y)
  /// @dev division by 0 has unspecified behavior, and must be checked externally
  /// @param x The dividend
  /// @param y The divisor
  /// @return z The quotient, ceil(x / y)
  function divRoundingUp(uint256 x, uint256 y) internal pure returns (uint256 z) {
    assembly {
      z := add(div(x, y), gt(mod(x, y), 0))
    }
  }
}

contracts/utils/CILAirdrop.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

/// @notice CIL Airdrop contract, each og can claim 7.1% of total amount.  (https://docs.cilistia.com/cil#tokenomics)
contract CILAirdrop is Ownable {
  using SafeERC20 for IERC20;

  /// @notice cil token addresses
  address public immutable CIL;

  /// @notice signer address
  address public immutable signer;

  /// @notice airdrop datas
  uint32 public openingTime;
  uint32 public closingTime;
  uint32 public ogNumber;
  uint256 public claimAmountPerWallet;

  /// @notice address => claimed timestamp
  mapping(address => uint256) public lastClaimedTime;

  struct Sig {
    bytes32 r;
    bytes32 s;
    uint8 v;
  }

  /// @notice fires when set period
  event SetPeriod(uint32 openingTime, uint32 closingTime);

  /// @notice fires when claimed
  event Claimed(address to, uint256 amount);

  /**
   * @param signer_ signer address
   * @param CIL_ cil token address
   */
  constructor(address signer_, address CIL_) {
    require(signer_ != address(0), "CILAirdrop: invalid signer address");
    require(CIL_ != address(0), "CILAirdrop: invalid CIL address");
    signer = signer_;
    CIL = CIL_;
  }

  /**
   * @dev returns airdrop state
   * @return bool returns true if airdrop is live
   */
  function isOpen() public view returns (bool) {
    return block.timestamp >= openingTime && block.timestamp < closingTime;
  }

  /**
   * @dev validates buy function variables
   * @return isValid ture -> valid, false -> invalid
   */
  function _isClaimParamValid(Sig calldata sig) private view returns (bool) {
    bytes32 messageHash = keccak256(abi.encodePacked(_msgSender()));

    bytes32 ethSignedMessageHash = keccak256(
      abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash)
    );

    return signer == ecrecover(ethSignedMessageHash, sig.v, sig.r, sig.s);
  }

  /**
   * @dev claim cil token
   * @param sig signature of signer
   * @return result return claimed cil token amount
   */
  function claim(Sig calldata sig) external returns (uint256 result) {
    require(isOpen(), "CILAirdrop: not open now");
    require(_isClaimParamValid(sig), "CILAirdrop: invalid signature");
    require(
      lastClaimedTime[_msgSender()] + 1 days <= block.timestamp,
      "CILAirdrop: already claimed today"
    );

    uint256 tokenAmount = claimAmountPerWallet;

    lastClaimedTime[_msgSender()] = block.timestamp;
    IERC20(CIL).safeTransfer(_msgSender(), tokenAmount);

    emit Claimed(_msgSender(), tokenAmount);
    return tokenAmount;
  }

  /**
   * @dev return balance of cil token
   * @return amount amount of cil token
   */
  function balance() public view returns (uint256) {
    return IERC20(CIL).balanceOf(address(this));
  }

  /**
   * @dev set airdrop settings
   * @param openingTime_ opening time of airdrop
   * @param closingTime_ closing time of airdrop
   * @param ogNumber_ number of og member
   */
  function setPeriod(
    uint32 openingTime_,
    uint32 closingTime_,
    uint32 ogNumber_
  ) external onlyOwner {
    // require(!isOpen(), "CILAirdrop: already opened");
    require(closingTime_ > openingTime_, "CILAirdrop: invalid time window");
    openingTime = openingTime_;
    closingTime = closingTime_;
    ogNumber = ogNumber_;
    claimAmountPerWallet = balance() / ogNumber_ / 14; // 14 days of airdrop duration

    emit SetPeriod(openingTime, closingTime);
  }

  /**
   * @dev withdraw all CIL to another address
   * @param recipient_ address to withdraw cil token
   */
  function withdraw(address recipient_) external onlyOwner {
    uint256 _balance = balance();
    IERC20(CIL).safeTransfer(recipient_, _balance);
  }
}

contracts/utils/CILPreSale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

/// @notice Cilistia preSale contract address. (https://docs.cilistia.com/cil#tokenomics)
contract CILPreSale is Ownable {
  using SafeERC20 for IERC20;

  /// @notice stable coin addresses
  address public immutable USDT;
  address public immutable USDC;

  /// @notice cil token address
  address public immutable CIL;

  /// @notice multiSig wallet address
  address public immutable multiSig1;
  /// @notice multiSig wallet address
  address public immutable multiSig2;
  /// @notice multiSig wallet address
  address public immutable multiSig3;

  /// @notice signer address
  address public immutable signer;

  /// @notice price per CIL
  uint256 public pricePerCIL = 500;

  /// @notice preSale period
  uint32 public openingTime;
  uint32 public closingTime;

  struct Sig {
    bytes32 r;
    bytes32 s;
    uint8 v;
  }

  /// @notice fires when buy CIL token
  event Buy(
    address indexed _executor,
    string indexed _tokenNameToDeposit,
    uint256 _deposit,
    uint256 _withdraw
  );

  /// @notice fires when set preSale period
  event SetPeriod(uint32 openingTime, uint32 closingTime);

  /// @notice fires when change price
  event PriceChanged(uint256 price);

  /**
   * @param signer_ signer address
   * @param multiSig1_ multi sign address
   * @param multiSig2_ multi sign address
   * @param USDT_ usdt address
   * @param USDC_ usdc address
   * @param CIL_ cil token address
   */
  constructor(
    address signer_,
    address multiSig1_,
    address multiSig2_,
    address multiSig3_,
    address USDT_,
    address USDC_,
    address CIL_
  ) {
    require(signer_ != address(0), "CILPreSale: invalid signer address");
    require(multiSig1_ != address(0), "CILPreSale: invalid multiSig address");
    require(multiSig2_ != address(0), "CILPreSale: invalid multiSig address");
    require(multiSig3_ != address(0), "CILPreSale: invalid multiSig address");
    require(USDT_ != address(0), "CILPreSale: invalid USDT address");
    require(USDC_ != address(0), "CILPreSale: invalid USDC address");
    require(CIL_ != address(0), "CILPreSale: invalid CIL address");
    signer = signer_;
    multiSig1 = multiSig1_;
    multiSig2 = multiSig2_;
    multiSig3 = multiSig3_;
    USDT = USDT_;
    USDC = USDC_;
    CIL = CIL_;
  }

  /**
   * @dev returns airdrop state
   * @return bool returns true if airdrop is live
   */
  function isOpen() public view returns (bool) {
    return block.timestamp >= openingTime && block.timestamp < closingTime;
  }

  /**
   * @dev validates buy function variables
   * @param amountToDeposit_ deposit token amount
   * @param tokenNameToDeposit_ token name to deposit
   * @param sig_ signature of backend wallet
   * @return isValid ture -> valid, false -> invalid
   */
  function _isBuyParamValid(
    uint256 amountToDeposit_,
    string memory tokenNameToDeposit_,
    Sig calldata sig_
  ) private view returns (bool) {
    bytes32 messageHash = keccak256(
      abi.encodePacked(_msgSender(), amountToDeposit_, tokenNameToDeposit_)
    );

    bytes32 ethSignedMessageHash = keccak256(
      abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash)
    );

    return signer == ecrecover(ethSignedMessageHash, sig_.v, sig_.r, sig_.s);
  }

  /**
   * @dev buy cil token with stable coins such as usdt, usdc
   * @param amountToDeposit_ deposit token amount
   * @param tokenNameToDeposit_ token name to deposit
   * @param sig_ signature of backend wallet
   * @return result bought cil token amount
   */
  function buy(
    uint256 amountToDeposit_,
    string memory tokenNameToDeposit_,
    Sig calldata sig_
  ) external returns (uint256 result) {
    require(isOpen(), "CILPreSale: not open now");
    require(
      _isBuyParamValid(amountToDeposit_, tokenNameToDeposit_, sig_),
      "CILPreSale: invalid signature"
    );

    address tokenToDeposit;

    if (keccak256(abi.encodePacked(tokenNameToDeposit_)) == keccak256(abi.encodePacked("USDT")))
      tokenToDeposit = USDT;
    else if (
      keccak256(abi.encodePacked(tokenNameToDeposit_)) == keccak256(abi.encodePacked("USDC"))
    ) tokenToDeposit = USDC;
    else revert("CILPreSale: incorrect deposit token");

    uint256 tokenDecimalToDeposit = IERC20Metadata(tokenToDeposit).decimals();
    uint256 multiplier = IERC20Metadata(CIL).decimals() - tokenDecimalToDeposit;
    uint256 currentAmountInUSD = (IERC20(CIL).balanceOf(_msgSender()) * pricePerCIL) /
      100 /
      (10 ** multiplier);

    require(
      amountToDeposit_ + currentAmountInUSD <= 1000 * (10 ** tokenDecimalToDeposit),
      "CILPreSale: max deposit amount is $1000 per wallet"
    );

    uint256 _balance = balance();
    uint256 amountWithdrawalCIL = (amountToDeposit_ * (10 ** multiplier) * 100) / pricePerCIL;
    require(amountWithdrawalCIL <= _balance, "CILPreSale: insufficient withdrawal amount");
    require(
      IERC20(tokenToDeposit).balanceOf(_msgSender()) >= amountToDeposit_,
      "CILPreSale: insufficient deposit balance"
    );

    uint256 amount1 = (amountToDeposit_ * 7) / 10;
    uint256 amount3 = amountToDeposit_ / 10;
    uint256 amount2 = amountToDeposit_ - amount1 - amount3;

    IERC20(tokenToDeposit).safeTransferFrom(_msgSender(), multiSig1, amount1);
    IERC20(tokenToDeposit).safeTransferFrom(_msgSender(), multiSig2, amount2);
    IERC20(tokenToDeposit).safeTransferFrom(_msgSender(), multiSig3, amount3);

    IERC20(CIL).safeTransfer(_msgSender(), amountWithdrawalCIL);

    emit Buy(_msgSender(), tokenNameToDeposit_, amountToDeposit_, amountWithdrawalCIL);

    return amountWithdrawalCIL;
  }

  /**
   * @dev return balance of cil token
   * @return amount amount of cil token
   */
  function balance() public view returns (uint256) {
    return IERC20(CIL).balanceOf(address(this));
  }

  /**
   * @dev set preSale settings
   * @param openingTime_ opening time of airdrop
   * @param closingTime_ closing time of airdrop
   */
  function setPeriod(uint32 openingTime_, uint32 closingTime_) external onlyOwner {
    // require(!isOpen(), "CILPreSale: already opened");
    require(closingTime_ > openingTime_, "CILPreSale: invalid time window");
    openingTime = openingTime_;
    closingTime = closingTime_;

    emit SetPeriod(openingTime, closingTime);
  }

  /**
   * @dev withdraw all CIL to another address
   * @param recipient_ address to withdraw cil token
   */
  function withdraw(address recipient_) external onlyOwner {
    uint256 _balance = balance();
    IERC20(CIL).safeTransfer(recipient_, _balance);
  }

  /**
   * @dev renounce price of CIL ($ per CIL)
   * @param priceCIL_ price of the cil token
   */
  function renouncePrice(uint256 priceCIL_) external onlyOwner {
    require(priceCIL_ > 0, "CILPreSale: price must be greater than zero");
    pricePerCIL = priceCIL_;

    emit PriceChanged(priceCIL_);
  }
}

contracts/utils/LiquidityExtension.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.9;

import {IUniswapV3Pool} from "../uniswap-contracts/interfaces/IUniswapV3Pool.sol";
import {TickMath} from "../uniswap-contracts/libraries/TickMath.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import {TransferHelper} from "../uniswap-contracts/libraries/TransferHelper.sol";
import {INonfungiblePositionManager} from "../uniswap-contracts/interfaces/INonfungiblePositionManager.sol";

contract LiquidityExtension is IERC721Receiver {
  address public immutable CIL;
  address public immutable WETH;

  uint24 public constant poolFee = 3000;

  INonfungiblePositionManager public immutable nonfungiblePositionManager;

  /// @notice Represents the deposit of an NFT
  struct Deposit {
    address owner;
    uint128 liquidity;
    address token0;
    address token1;
  }

  /// @dev deposits[tokenId] => Deposit
  mapping(uint256 => Deposit) public deposits;

  constructor(INonfungiblePositionManager _nonfungiblePositionManager, address cil) {
    nonfungiblePositionManager = _nonfungiblePositionManager;
    CIL = cil;
    WETH = nonfungiblePositionManager.WETH9();
  }

  // Implementing `onERC721Received` so this contract can receive custody of erc721 tokens
  function onERC721Received(
    address operator,
    address,
    uint256 tokenId,
    bytes calldata
  ) external override returns (bytes4) {
    // get position information

    _createDeposit(operator, tokenId);

    return this.onERC721Received.selector;
  }

  function _createDeposit(address owner, uint256 tokenId) internal {
    (
      ,
      ,
      address token0,
      address token1,
      ,
      ,
      ,
      uint128 liquidity,
      ,
      ,
      ,

    ) = nonfungiblePositionManager.positions(tokenId);

    // set the owner and data for position
    // operator is msg.sender
    deposits[tokenId] = Deposit({
      owner: owner,
      liquidity: liquidity,
      token0: token0,
      token1: token1
    });
  }

  /**
   * @notice Calls the mint function defined in periphery
   * @param amountCILToMint The amount of token0 to deposit
   * @param amountWETHToMint The amount of token1 to deposit
   * @return tokenId The id of the newly minted ERC721
   * @return liquidity The amount of liquidity for the position
   * @return cilAmount The amount of token0
   * @return wethAmount The amount of token1
   */
  function mintNewPosition(
    uint256 amountCILToMint,
    uint256 amountWETHToMint
  ) external returns (uint256 tokenId, uint128 liquidity, uint256 cilAmount, uint256 wethAmount) {
    bool isFirst = WETH > CIL;

    // transfer tokens to contract
    TransferHelper.safeTransferFrom(CIL, msg.sender, address(this), amountCILToMint);
    TransferHelper.safeTransferFrom(WETH, msg.sender, address(this), amountWETHToMint);

    // Approve the position manager
    TransferHelper.safeApprove(CIL, address(nonfungiblePositionManager), amountCILToMint);
    TransferHelper.safeApprove(WETH, address(nonfungiblePositionManager), amountWETHToMint);

    int24 maxTicks = (TickMath.MAX_TICK / 60) * 60;

    INonfungiblePositionManager.MintParams memory params = INonfungiblePositionManager.MintParams({
      token0: isFirst ? CIL : WETH,
      token1: isFirst ? WETH : CIL,
      fee: poolFee,
      tickLower: -1 * maxTicks,
      tickUpper: maxTicks,
      amount0Desired: isFirst ? amountCILToMint : amountWETHToMint,
      amount1Desired: isFirst ? amountWETHToMint : amountCILToMint,
      amount0Min: 0,
      amount1Min: 0,
      recipient: address(this),
      deadline: block.timestamp
    });

    uint256 amount0;
    uint256 amount1;
    // Note that the pool defined by CIL/WETH and fee tier 0.3% must already be created and initialized in order to mint
    (tokenId, liquidity, amount0, amount1) = nonfungiblePositionManager.mint(params);

    // Create a deposit
    _createDeposit(msg.sender, tokenId);

    cilAmount = isFirst ? amount0 : amount1;
    wethAmount = isFirst ? amount1 : amount0;

    // Remove allowance and refund in both assets.
    if (cilAmount < amountCILToMint) {
      TransferHelper.safeApprove(CIL, address(nonfungiblePositionManager), 0);
      uint256 refund0 = amountCILToMint - cilAmount;
      TransferHelper.safeTransfer(CIL, msg.sender, refund0);
    }

    if (wethAmount < amountWETHToMint) {
      TransferHelper.safeApprove(WETH, address(nonfungiblePositionManager), 0);
      uint256 refund1 = amountWETHToMint - wethAmount;
      TransferHelper.safeTransfer(WETH, msg.sender, refund1);
    }
  }

  /**
   * @notice Collects the fees associated with provided liquidity
   * @dev The contract must hold the erc721 token before it can collect fees
   * @param tokenId The id of the erc721 token
   * @return amount0 The amount of fees collected in token0
   * @return amount1 The amount of fees collected in token1
   */
  function collectAllFees(uint256 tokenId) public returns (uint256 amount0, uint256 amount1) {
    // Caller must own the ERC721 position, meaning it must be a deposit

    // set amount0Max and amount1Max to uint256.max to collect all fees
    // alternatively can set recipient to msg.sender and avoid another transaction in `sendToOwner`
    INonfungiblePositionManager.CollectParams memory params = INonfungiblePositionManager
      .CollectParams({
        tokenId: tokenId,
        recipient: address(this),
        amount0Max: type(uint128).max,
        amount1Max: type(uint128).max
      });

    (amount0, amount1) = nonfungiblePositionManager.collect(params);

    // send collected feed back to owner
    _sendToOwner(tokenId, amount0, amount1);
  }

  /**
   * @notice A function that decreases the current liquidity by half. An example to show how to call the `decreaseLiquidity` function defined in periphery.
   * @param tokenId The id of the erc721 token
   * @param liquidity The liquidity amount to remove
   * @return amountCIL The amount received back in token0
   * @return amountWETH The amount returned back in token1
   */
  function decreaseLiquidity(
    uint256 tokenId,
    uint128 liquidity
  ) external returns (uint256 amountCIL, uint256 amountWETH) {
    bool isFirst = WETH > CIL;

    // caller must be the owner of the NFT
    require(msg.sender == deposits[tokenId].owner, "Not the owner");
    // liquidity should less than total
    require(liquidity <= deposits[tokenId].liquidity, "Invalid liquidity amount");

    // amountCILMin and amountWETHMin are price slippage checks
    // if the amount received after burning is not greater than these minimums, transaction will fail
    INonfungiblePositionManager.DecreaseLiquidityParams memory params = INonfungiblePositionManager
      .DecreaseLiquidityParams({
        tokenId: tokenId,
        liquidity: liquidity,
        amount0Min: 0,
        amount1Min: 0,
        deadline: block.timestamp
      });

    uint256 amount0;
    uint256 amount1;
    (amount0, amount1) = nonfungiblePositionManager.decreaseLiquidity(params);

    collectAllFees(tokenId);

    amountCIL = isFirst ? amount0 : amount1;
    amountWETH = isFirst ? amount1 : amount0;
  }

  /// @notice Increases liquidity in the current range
  /// @dev Pool must be initialized already to add liquidity
  /// @param tokenId The id of the erc721 token
  /// @param amountCIL The amount to add of token0
  /// @param amountWETH The amount to add of token1
  function increaseLiquidityCurrentRange(
    uint256 tokenId,
    uint256 amountAddCIL,
    uint256 amountAddWETH
  ) external returns (uint128 liquidity, uint256 amountCIL, uint256 amountWETH) {
    bool isFirst = WETH > CIL;

    TransferHelper.safeTransferFrom(
      deposits[tokenId].token0,
      msg.sender,
      address(this),
      amountAddCIL
    );
    TransferHelper.safeTransferFrom(
      deposits[tokenId].token1,
      msg.sender,
      address(this),
      amountAddWETH
    );

    TransferHelper.safeApprove(
      deposits[tokenId].token0,
      address(nonfungiblePositionManager),
      amountAddCIL
    );
    TransferHelper.safeApprove(
      deposits[tokenId].token1,
      address(nonfungiblePositionManager),
      amountAddWETH
    );

    INonfungiblePositionManager.IncreaseLiquidityParams memory params = INonfungiblePositionManager
      .IncreaseLiquidityParams({
        tokenId: tokenId,
        amount0Desired: isFirst ? amountAddCIL : amountAddWETH,
        amount1Desired: isFirst ? amountAddWETH : amountAddCIL,
        amount0Min: 0,
        amount1Min: 0,
        deadline: block.timestamp
      });

    uint256 amount0;
    uint256 amount1;
    (liquidity, amount0, amount1) = nonfungiblePositionManager.increaseLiquidity(params);

    amountCIL = isFirst ? amount0 : amount1;
    amountWETH = isFirst ? amount1 : amount0;
  }

  /// @notice Transfers funds to owner of NFT
  /// @param tokenId The id of the erc721
  /// @param amount0 The amount of token0
  /// @param amount1 The amount of token1
  function _sendToOwner(uint256 tokenId, uint256 amount0, uint256 amount1) internal {
    // get owner of contract
    address owner = deposits[tokenId].owner;

    address token0 = deposits[tokenId].token0;
    address token1 = deposits[tokenId].token1;

    // send collected fees to owner
    TransferHelper.safeTransfer(token0, owner, amount0);
    TransferHelper.safeTransfer(token1, owner, amount1);
  }

  /// @notice Transfers the NFT to the owner
  /// @param tokenId The id of the erc721
  function retrieveNFT(uint256 tokenId) external {
    // must be the owner of the NFT
    require(msg.sender == deposits[tokenId].owner, "Not the owner");
    // transfer ownership to original owner
    nonfungiblePositionManager.safeTransferFrom(address(this), msg.sender, tokenId);
    //remove information related to tokenId
    delete deposits[tokenId];
  }
}

contracts/utils/ReentrancyGuard.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

/**
 * @dev Contract module that helps prevent reentrant calls to a function.
 *
 * Inheriting from `ReentrancyGuard` will make the {nonReentrant} modifier
 * available, which can be applied to functions to make sure there are no nested
 * (reentrant) calls to them.
 *
 * Note that because there is a single `nonReentrant` guard, functions marked as
 * `nonReentrant` may not call one another. This can be worked around by making
 * those functions `private`, and then adding `external` `nonReentrant` entry
 * points to them.
 *
 * TIP: If you would like to learn more about reentrancy and alternative ways
 * to protect against it, check out our blog post
 * https://blog.openzeppelin.com/reentrancy-after-istanbul/[Reentrancy After Istanbul].
 */
contract ReentrancyGuard {
  // Booleans are more expensive than uint256 or any type that takes up a full
  // word because each write operation emits an extra SLOAD to first read the
  // slot's contents, replace the bits taken up by the boolean, and then write
  // back. This is the compiler's defense against contract upgrades and
  // pointer aliasing, and it cannot be disabled.

  // The values being non-zero value makes deployment a bit more expensive,
  // but in exchange the refund on every call to nonReentrant will be lower in
  // amount. Since refunds are capped to a percentage of the total
  // transaction's gas, it is best to keep them low in cases like this one, to
  // increase the likelihood of the full refund coming into effect.
  uint256 private constant _NOT_ENTERED = 1;
  uint256 private constant _ENTERED = 2;

  uint256 private _status;

  constructor() {
    _status = _NOT_ENTERED;
  }

  /**
   * @dev Prevents a contract from calling itself, directly or indirectly.
   * Calling a `nonReentrant` function from another `nonReentrant`
   * function is not supported. It is possible to prevent this from happening
   * by making the `nonReentrant` function external, and make it call a
   * `private` function that does the actual work.
   */
  modifier nonReentrant() {
    // On the first call to nonReentrant, _notEntered will be true
    require(_status != _ENTERED, "ReentrancyGuard: reentrant call");

    // Any calls to nonReentrant after this point will fail
    _status = _ENTERED;

    _;

    // By storing the original value once again, a refund is triggered (see
    // https://eips.ethereum.org/EIPS/eip-2200)
    _status = _NOT_ENTERED;
  }
}
